import React, { useState, useEffect } from 'react';
import { HamburgerIcon } from './svgs/HamburgerIcon';

export const Nav = ({screenWidth}) => {
    const navHeadings = ["About", "Knowledge", "Projects", "Contact"];

    // console.log('screenWidth', screenWidth)

    // const [hamburgerPresent, setHamburgerPresent] = useState(false)
    // const [hamburgerPresent, setHamburgerPresent] = useState(screenWidth < 400 ? true : false)
    const hamburgerPresent = screenWidth < 400;

    // const [hamburgerMenuOpen, sethamburgerMenuOpen] = useState(false)
    const [hamburgerMenuOpen, sethamburgerMenuOpen] = useState(false)


    // const handleScreenWidth = () => {
    //     // Accounts for screen size changes while in use, including closing vertical burger menu after breakpoint
    //     // setHamburgerPresent(window.outerWidth < 400 ? true : false)
    //     setHamburgerPresent(screenWidth < 400 ? true : false)
    //     if (screenWidth >= 400) {
    //         sethamburgerMenuOpen(false)
    //     }
    // }

    const toggleHamburgerMenu = () => {
        // Toggles vertical burger nav list
        sethamburgerMenuOpen(hamburgerMenuOpen ? false : true)
    }

    // useEffect(() => {
    //     window.addEventListener("resize", handleScreenWidth)
    // })

    return (
        <nav id="menu" className={`${hamburgerPresent ? 'hamburgerPresent' : ''} ${hamburgerMenuOpen ? 'navbarExtend' : ''}`}>
            {hamburgerPresent ? (
                <div className='hamburgerContainer'>
                    <HamburgerIcon toggleHamburgerMenu={toggleHamburgerMenu} />
                </div>

            ) : (
                <div className='horizontalNavLinks'>
                    {navHeadings.map((navHeading, i) => {
                        const navRef = `#${navHeading.toLowerCase()}`
                        return <a key={i} href={navRef} className='horizontalNavLink'>{navHeading}</a>
                    })}
                </div>
            )}

            {hamburgerMenuOpen && (
                <div className='verticalNavLinks'>
                    {navHeadings.map((navHeading, i) => {
                        const navRef = `#${navHeading.toLowerCase()}`
                        return <a key={i} href={navRef} className='verticalNavLink'>{navHeading}</a>
                    })}
                </div>
            )}
        </nav>
    )
}
