import React, { useState, useEffect } from 'react';
import { HamburgerIcon } from './svgs/HamburgerIcon';

export const Nav = ({ screenWidth }) => {
    const navHeadings = ["About", "Knowledge", "Projects", "Contact"];

    const hamburgerPresent = screenWidth < 400;

    const [hamburgerMenuOpen, sethamburgerMenuOpen] = useState(false)
    const [navVisible, setNavVisible] = useState(false)

    const handleScreenWidth = () => {
        // combined with useEffect to close nav list if screen size changes and burger menu disappears (e.g. from portrait to landscape)
        if (screenWidth >= 400) {
            sethamburgerMenuOpen(false)
        }
    }

    const toggleHamburgerMenu = () => {
        // Toggles vertical burger nav list
        sethamburgerMenuOpen(hamburgerMenuOpen ? false : true)
    }

    const toggleNavVisibility = (val) => {
        setNavVisible(val);
    }

    useEffect(() => {
        window.addEventListener("resize", handleScreenWidth)
    })

    return (
        <div id='navContainer'
            onMouseOver={(e) => toggleNavVisibility(true)}
            onMouseLeave={(e) => toggleNavVisibility(false)}
        >
            {/* {navVisible && (
                <nav 
                    id="menu" 
                    className={`${hamburgerPresent ? 'hamburgerPresent' : ''} ${hamburgerMenuOpen ? 'navbarExtend' : ''} `} 
                >
                    {hamburgerPresent ? (
                        <div className='hamburgerContainer'>
                            <HamburgerIcon toggleHamburgerMenu={toggleHamburgerMenu} />
                        </div>

                    ) : (
                        <div className='horizontalNavLinks'>
                            {navHeadings.map((navHeading, i) => {
                                const navRef = `#${navHeading.toLowerCase()}`
                                return <a key={i} href={navRef} className='horizontalNavLink'>{navHeading}</a>
                            })}
                        </div>
                    )}

                    {hamburgerMenuOpen && (
                        <div className='verticalNavLinks'>
                            {navHeadings.map((navHeading, i) => {
                                const navRef = `#${navHeading.toLowerCase()}`
                                return <a key={i} href={navRef} className='verticalNavLink'>{navHeading}</a>
                            })}
                        </div>
                    )}
                </nav>
            )} */}
            {/* <nav
                id="menu"
                className={`${hamburgerPresent ? 'hamburgerPresent' : ''} ${hamburgerMenuOpen ? 'navbarExtend' : ''} ${navVisible ? 'navVisibility' : ''}`}
            > */}
            <nav
                id="menu"
                className={`${hamburgerPresent ? 'hamburgerPresent' : ''} ${hamburgerMenuOpen ? 'navbarExtend' : ''} navVisibility`}
            >
                {hamburgerPresent ? (
                    <div className='hamburgerContainer'>
                        <HamburgerIcon toggleHamburgerMenu={toggleHamburgerMenu} />
                    </div>

                ) : (
                    <div className='horizontalNavLinks'>
                        {navHeadings.map((navHeading, i) => {
                            const navRef = `#${navHeading.toLowerCase()}`
                            return (
                                <div key={i} className='navLinkContainer'>
                                    <a href={navRef} className='horizontalNavLink'>{navHeading}</a>
                                </div>
                            )
                        })}
                    </div>
                )}

                {hamburgerMenuOpen && (
                    <div className='verticalNavLinks'>
                        {navHeadings.map((navHeading, i) => {
                            const navRef = `#${navHeading.toLowerCase()}`
                            return <a key={i} href={navRef} className='verticalNavLink'>{navHeading}</a>
                        })}
                    </div>
                )}
            </nav>
        </div>
    )

    // return (
