import React, { useState, useEffect } from 'react';
import { HamburgerIcon } from './svgs/HamburgerIcon';

export const Nav = () => {
    const navHeadings = ["About", "Knowledge", "Projects", "Contact"];

    const [hamburgerPresent, setHamburgerPresent] = useState(false)

    const [hamburgerMenuOpen, sethamburgerMenuOpen] = useState(false)


    const handleScreenWidth = () => {
        setHamburgerPresent(window.outerWidth < 400 ? true : false)
    }

    const toggleHamburgerMenu = () => {
        hamburgerMenuOpen ? sethamburgerMenuOpen(false) : sethamburgerMenuOpen(true);
    }

    useEffect(() => {
        window.addEventListener("resize", handleScreenWidth)
    })

    console.log('hamburgerMenuOpen', hamburgerMenuOpen)

    return (
        <nav id="menu" className={hamburgerPresent ? 'hamburgerPresent' : ''}>
            {/* {navHeadings.map((navHeading, i) => {
                const navRef = `#${navHeading.toLowerCase()}`
                return <a key={i} href={navRef}>{navHeading}</a>
            })} */}
            {/* {!hamburgerPresent && (
                navHeadings.map((navHeading, i) => {
                    const navRef = `#${navHeading.toLowerCase()}`
                    return <a key={i} href={navRef}>{navHeading}</a>
                })
            )} */}
            {hamburgerPresent ? (
                <HamburgerIcon onClick={() => toggleHamburgerMenu()} />
            ) : (
                <div className='navLinks'>
                    {navHeadings.map((navHeading, i) => {
                        const navRef = `#${navHeading.toLowerCase()}`
                        return <a key={i} href={navRef}>{navHeading}</a>
                    })}
                </div>

            )}


        </nav>
    )
}
